import React, { useState, useMemo } from "react";
import { red } from "@material-ui/core/colors";
import { Grid, Typography } from "@material-ui/core";
// import ToggleButton from "@material-ui/lab/ToggleButton";
// import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";

const BurgerModalSetRequests = props => {
  // const [sides, setSides] = useState([
  //   { id: "side1", name: "양념감자(소)", check: true, addPrice: 0 },
  //   { id: "side2", name: "양념감자(중)", check: false, addPrice: 1000 },
  //   { id: "side3", name: "치즈감자", check: false, addPrice: 1000 },
  //   { id: "side4", name: "어니언치즈감자", check: false, addPrice: 1000 }
  // ]);
  const [sideChoice, setSideChoice] = useState(0);
  const [beverageChoice, setBeverageChoice] = useState(0);
  const getTotalPrice = () => {
    //   let sidePrice = 0;
    //   sides.map(side => {
    //     if (side["check"]) sidePrice += side.addPrice;
    //   });
    //   console.log(sidePrice);
    //   setSideChoice(sidePrice);
    return props.defaultPrice + sideChoice + beverageChoice;
  };

  // const test123 = useMemo(() => getTotalPrice(), [sideChoice, beverageChoice]);
  // console.log(test123);

  // props.priceChanger(test123);

  props.priceChanger(getTotalPrice());

  // 태환이가 만들어준거!!-------
  // const reverseChoice = id => {
  //   let temp = [];
  //   sides.map(side => {
  //     if (side.id === id) {
  //       temp.push({
  //         id: side.id,
  //         name: side.name,
  //         check: true,
  //         addPrice: side.addPrice
  //       });
  //     } else {
  //       temp.push({
  //         id: side.id,
  //         name: side.name,
  //         check: false,
  //         addPrice: side.addPrice
  //       });
  //     }
  //   });
  //   setSides(temp);
  // };
  // ----------------------------

  return (
    <div>
      <Grid container>
        <Grid item xs={1} style={{ background: red[500] }}></Grid>
        <Grid item xs={11} style={{ background: red[100] }}>
          사이드
        </Grid>
      </Grid>
      <Grid container style={{ margin: 0, width: 900, padding: 20 }}>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => {
              setSideChoice(0);
              // reverseChoice(sides[0].id);
              // sides.map(side => {
              //   if (side["check"]) return console.log(side.id);
              // });
            }}
          >
            <p>양념감자(소)</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => {
              setSideChoice(1000);
              // reverseChoice(sides[1].id);
              // sides.map(side => {
              //   if (side["check"]) return console.log(side.id);
              // });
            }}
          >
            <p style={{ marginTop: 10, marginBottom: 10 }}>양념감자(중)</p>
            <p style={{ marginTop: 10, marginBottom: 10 }}>+1000</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => setSideChoice(1000)}
          >
            <p style={{ marginTop: 10, marginBottom: 10 }}>치즈감자</p>
            <p style={{ marginTop: 10, marginBottom: 10 }}>+1000</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => setSideChoice(1000)}
          >
            <p style={{ marginTop: 10, marginBottom: 10 }}>어니언치즈감자</p>
            <p style={{ marginTop: 10, marginBottom: 10 }}>+1000</p>
          </Typography>
        </Grid>
      </Grid>
      {/* ==================================================== */}
      <Grid container>
        <Grid item xs={1} style={{ background: red[500] }}></Grid>
        <Grid item xs={11} style={{ background: red[100] }}>
          음료
        </Grid>
      </Grid>
      <Grid container style={{ margin: 0, width: 900, padding: 20 }}>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => setBeverageChoice(0)}
          >
            <p>콜라</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => setBeverageChoice(0)}
          >
            <p>사이다</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => setBeverageChoice(400)}
          >
            <p style={{ marginTop: 10, marginBottom: 10 }}>오렌지쥬스</p>
            <p style={{ marginTop: 10, marginBottom: 10 }}>+400</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => setBeverageChoice(400)}
          >
            <p style={{ marginTop: 10, marginBottom: 10 }}>블렉베리쥬스</p>
            <p style={{ marginTop: 10, marginBottom: 10 }}>+400</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => setBeverageChoice(600)}
          >
            <p style={{ marginTop: 10, marginBottom: 10 }}>레몬에이드</p>
            <p style={{ marginTop: 10, marginBottom: 10 }}>+600</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography
            gutterBottom
            variant="h5"
            component="h2"
            onClick={() => setBeverageChoice(600)}
          >
            <p style={{ marginTop: 10, marginBottom: 10 }}>청포도에이드</p>
            <p style={{ marginTop: 10, marginBottom: 10 }}>+600</p>
          </Typography>
        </Grid>
      </Grid>
      {/* ==================================================== */}
      <Grid container>
        <Grid item xs={1} style={{ background: red[500] }}></Grid>
        <Grid item xs={11} style={{ background: red[100] }}>
          요청사항
        </Grid>
      </Grid>
      <Grid container style={{ margin: 0, width: 900, padding: 20 }}>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography gutterBottom variant="h5" component="h2">
            <p>요청없음</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography gutterBottom variant="h5" component="h2">
            <p>피클제거</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography gutterBottom variant="h5" component="h2">
            <p>양파제거</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography gutterBottom variant="h5" component="h2">
            <p>피클,양파제거</p>
          </Typography>
        </Grid>
      </Grid>
      {/* ==================================================== */}
    </div>
  );
};

export default BurgerModalSetRequests;
