import React, { useState } from "react";
import { red } from "@material-ui/core/colors";
import {
  Grid,
  Typography,
  Button,
  Card,
  CardActionArea,
  CardMedia,
  CardContent
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  root: {
    width: 200,
    maxWidth: 345,
    boxShadow: 0
  },
  btnPosition: {
    textAlign: "center",
    paddingTop: 50
  },
  btnGridHeight: {
    height: 180
  },
  btnCommit: {
    color: "white",
    background: "red",
    height: 100,
    width: 200
  },
  btnCancel: {
    color: "white",
    background: "grey",
    height: 100,
    width: 200
  }
});

const BurgerModalSingleRequests = props => {
  const classes = useStyles();

  const [requests, setRequests] = useState([
    {
      id: "request1",
      name: "요청없음",
      check: true,
      back: nothing,
      img: check
    },
    {
      id: "request2",
      name: "피클제거",
      check: false,
      back: noPickle,
      img: checkNone
    },
    {
      id: "request3",
      name: "양파제거",
      check: false,
      back: noOnion,
      img: checkNone
    },
    {
      id: "request4",
      name: "피클, 양파제거",
      check: false,
      back: noBoth,
      img: checkNone
    }
  ]);

  const requestsHtml = requests.map(request => {
    return (
      <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
        <Card className={classes.root} onClick={() => pickRequest(request.id)}>
          <CardActionArea>
            <CardMedia
              component="img"
              height="140"
              image={request.img}
              style={{
                height: 200,
                width: 200,
                backgroundImage: `url(${request.back})`
              }}
            />
            <CardContent style={{ padding: 0 }}>
              <Typography gutterBottom variant="h5" component="h2">
                <p style={{ marginTop: 10, marginBottom: 10 }}>
                  {request.name}
                </p>
              </Typography>
            </CardContent>
          </CardActionArea>
        </Card>
      </Grid>
    );
  });

  const pickRequest = id => {
    let temp = [];
    requests.map(request => {
      if (request.id === id) {
        temp.push({
          id: request.id,
          name: request.name,
          check: true,
          back: request.back,
          img: check
        });
      } else {
        temp.push({
          id: request.id,
          name: request.name,
          check: false,
          back: request.back,
          img: checkNone
        });
      }
    });
    setRequests(temp);
  };

  return (
    <div>
      <Grid container>
        <Grid item xs={1} style={{ background: red[500] }}></Grid>
        <Grid item xs={11} style={{ background: red[100] }}>
          요청사항
        </Grid>
      </Grid>
      <Grid container style={{ margin: 0, width: 900, padding: 20 }}>
        {requestsHtml}
        {/* <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography gutterBottom variant="h5" component="h2">
            <p>요청없음</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography gutterBottom variant="h5" component="h2">
            <p>피클제거</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography gutterBottom variant="h5" component="h2">
            <p>양파제거</p>
          </Typography>
        </Grid>
        <Grid item xs={3} style={{ margin: 0, textAlign: "center" }}>
          <Typography gutterBottom variant="h5" component="h2">
            <p>피클,양파제거</p>
          </Typography>
        </Grid> */}
      </Grid>
      {/* ==================================================== */}
      <Grid container className={classes.btnGridHeight}>
        <Grid item xs={6} className={classes.btnPosition}>
          <Button
            className={classes.btnCancel}
            variant="contained"
            onClick={props.handleCloseSingle}
          >
            <Typography variant="h5">취소</Typography>
          </Button>
        </Grid>
        <Grid item xs={6} className={classes.btnPosition}>
          <Button className={classes.btnCommit} variant="contained">
            <Typography variant="h5">확인</Typography>
          </Button>
        </Grid>
      </Grid>
    </div>
  );
};

export default BurgerModalSingleRequests;
