import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { red } from "@material-ui/core/colors";
import {
  Grid,
  Typography,
  Card,
  CardContent,
  CardActionArea,
  CardActions,
  CardMedia,
  Button
} from "@material-ui/core";
import check from "../img/burgerModal/check.png";
import chips1 from "../img/burgerModal/chips1.png";
import chips2 from "../img/burgerModal/chips2.png";
import cheese from "../img/burgerModal/cheese.png";
import onion from "../img/burgerModal/onion.png";
import coke from "../img/burgerModal/coke.png";
import sprite from "../img/burgerModal/sprite.png";
import orange from "../img/burgerModal/orange.png";
import blackberry from "../img/burgerModal/blackberry.png";
import lemon from "../img/burgerModal/lemon.png";
import muscat from "../img/burgerModal/muscat.png";
// import ToggleButton from "@material-ui/lab/ToggleButton";
// import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";

const useStyles = makeStyles({
  root: {
    width: 200,
    maxWidth: 345,
    boxShadow: 0
  }
});

const BurgerModalSetRequests = props => {
  const classes = useStyles();

  const [sides, setSides] = useState([
    {
      id: "side1",
      name: "양념감자(소)",
      check: true,
      addPrice: 0,
      img: check
    },
    {
      id: "side2",
      name: "양념감자(중)",
      check: false,
      addPrice: 1000,
      img: ""
    },
    {
      id: "side3",
      name: "치즈감자",
      check: false,
      addPrice: 1000,
      img: ""
    },
    {
      id: "side4",
      name: "어니언치즈감자",
      check: false,
      addPrice: 1000,
      img: ""
    }
  ]);
  const [beverages, setBeverages] = useState([
    { id: "beverage1", name: "콜라", check: true, addPrice: 0 },
    { id: "beverage2", name: "사이다", check: false, addPrice: 0 },
    { id: "beverage3", name: "오렌지쥬스", check: false, addPrice: 400 },
    { id: "beverage4", name: "블렉베리쥬스", check: false, addPrice: 400 },
    { id: "beverage5", name: "레몬에이드", check: false, addPrice: 600 },
    { id: "beverage6", name: "청포도에이드", check: false, addPrice: 600 }
  ]);
  const [sidePrice, setSidePrice] = useState(0);
  const [beveragePrice, setBeveragePrice] = useState(0);

  const getTotalPrice = () => {
    return props.defaultPrice + sidePrice + beveragePrice;
  };

  const pickSide = id => {
    let temp = [];
    sides.map(side => {
      if (side.id === id) {
        temp.push({
          id: side.id,
          name: side.name,
          check: true,
          addPrice: side.addPrice,
          img: check
        });

        setSidePrice(side.addPrice);
      } else {
        temp.push({
          id: side.id,
          name: side.name,
          check: false,
          addPrice: side.addPrice,
          img: ""
        });
      }
    });
    console.log(sides);
    setSides(temp);
  };

  const pickBeverage = id => {
    let temp = [];
    beverages.map(beverage => {
      if (beverage.id === id) {
        temp.push({
          id: beverage.id,
          name: beverage.name,
          check: true,
          addPrice: beverage.addPrice
        });
        setBeveragePrice(beverage.addPrice);
      } else {
        temp.push({
          id: beverage.id,
          name: beverage.name,
          check: false,
          addPrice: beverage.addPrice
        });
      }
    });
    setBeverages(temp);
  };

  props.priceChanger(getTotalPrice());
  // console.log(sides[0].img);
  // console.log(typeof sides[0].img);

  return (
