import React, { useState } from "react";
import {
  makeStyles,
  Button,
  Typography,
  Grid,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  TableContainer,
  TableHead,
  TableBody,
  TableRow,
  TableCell
} from "@material-ui/core";
import { Fastfood, Storefront } from "@material-ui/icons";
import { red, grey } from "@material-ui/core/colors";

const useStyles = makeStyles(theme => ({
  btnPayment: {
    color: "white",
    background: "red",
    height: 100,
    width: "100%"
  },
  dialogTitle: {
    backgroundColor: "red",
    width: "840px",
    height: 69,
    color: "white",
    padding: 0
  },
  dialogBody: {
    width: "840px"
  },
  titleCss: {
    position: "absolute",
    left: theme.spacing(2),
    margin: 0,
    fontSize: 35,
    padding: 5
  },
  tableHeadCell: {
    textAlign: "center",
    fontSize: 25
  },
  btnWhere: {
    background: grey[300],
    width: 300,
    height: 150,
    fontSize: 50
  }
}));

const PaymentModal = ({ orderList }) => {
  const classes = useStyles();

  const [open, setOpen] = useState(false);
  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const [openOrderFirst, setOpenOrderFirst] = useState(false);
  const handleClickOpenOrderFirst = () => {
    setOpenOrderFirst(true);
  };
  const handleCloseOrderFirst = () => {
    setOpenOrderFirst(false);
  };

  const handleClick = () => {
    if (orderList.length === 0) {
      handleClickOpenOrderFirst();
    } else {
      handleClickOpen();
    }
    console.log(orderList);
  };

  return (
    <div>
      <Button className={classes.btnPayment} onClick={handleClick}>
        결제하기
      </Button>
      {/* 주문 확인 및 식사 장소 선택 모달*/}
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
        maxWidth="xl"
      >
        <DialogTitle id="alert-dialog-title" className={classes.dialogTitle}>
          <p className={classes.titleCss}>결제하기</p>
        </DialogTitle>
        <DialogContent>
          <DialogContentText
            id="alert-dialog-description"
            style={{ textAlign: "center" }}
          >
            <Typography variant="h5" style={{ color: "black", margin: 20 }}>
              주문내역을 확인 후 식사 장소를 선택하세요.
            </Typography>
          </DialogContentText>
          <TableContainer
            style={{
              height: 500,
              borderTop: "2px solid",
              borderBottom: "2px solid"
            }}
          >
            <TableHead>
              <TableRow style={{ background: red[100] }}>
                <TableCell style={{ minWidth: 390, fontSize: 25 }}>
                  제품명
                </TableCell>
                <TableCell
                  style={{ minWidth: 200 }}
                  className={classes.tableHeadCell}
                >
                  수량
                </TableCell>
                <TableCell
                  style={{ minWidth: 200 }}
                  className={classes.tableHeadCell}
                >
                  금액
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {orderList.map(order => {
                return (
                  <TableRow key={order.id}>
                    <TableCell>
                      {order.contents.map((content, idx) => {
                        if (idx === order.contents.length - 1) {
                          return content;
                        } else {
                          return content + ", ";
                        }
                      })}
                    </TableCell>
                    <TableCell style={{ textAlign: "center" }}>
                      {order.cnt}
                    </TableCell>
                    <TableCell style={{ textAlign: "center" }}>
                      {order.price}
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </TableContainer>
        </DialogContent>
        <Grid container style={{ height: 200 }}>
          <Grid item xs={6} style={{ textAlign: "center" }}>
            <Button onClick={handleClose} className={classes.btnWhere}>
              <Fastfood style={{ fontSize: 50, marginRight: 10 }} />
              <div>포장</div>
            </Button>
          </Grid>
          <Grid item xs={6} justify="center">
            <Button onClick={handleClose} className={classes.btnWhere}>
              <Storefront style={{ fontSize: 50, marginRight: 10 }} />
              <div>매장</div>
            </Button>
          </Grid>
        </Grid>
      </Dialog>

      {/* 주문내역이 없을 떄 보여주는 모달 */}
      <Dialog
        open={openOrderFirst}
        onClose={handleCloseOrderFirst}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            제품을 선택해 주세요.
          </DialogContentText>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default PaymentModal;
