  const [beveragePrice, setBeveragePrice] = useState(0);

  const getTotalPrice = () => {
    return props.defaultPrice + sidePrice + beveragePrice;
  };

  const pickSide = id => {
    let temp = [];
    sides.map(side => {
      if (side.id === id) {
        temp.push({
          id: side.id,
          name: side.name,
          check: true,
          addPrice: side.addPrice,
          back: side.back,
          img: check
