import React, { useMemo } from "react";
import { makeStyles, Grid, Paper, Typography, Button } from "@material-ui/core";
import { grey } from "@material-ui/core/colors";
import PaymentModal from "./PaymentModal";

const useStyles = makeStyles(theme => ({
  // container: {
  //   flexGrow: 1,
  //   height: "620px",
  //   textAlign: "center",
  //   background: grey[400]
  // },
  // text: {
  //   color: "white",
  //   padding: "0 auto",
  //   margin: "0"
  // },
  paper: {
    padding: theme.spacing(1),
    textAlign: "center"
  },
  btnCancel: {
    color: "white",
    background: grey[700],
    height: 100,
    width: "100%"
  },
  btnPayment: {
    color: "white",
    background: "red",
    height: 100,
    width: "100%"
  }
  // listPaper: {
  //   height: 600,
  //   paddingTop: 20,
  //   paddingLeft: 20
  // }
}));

const Payment = ({ orderList, setOrderList }) => {
  const classes = useStyles();

  const getTotalCnt = () => {
    let total = 0;
    orderList.map(order => {
      total += order.cnt;
    });
    return total;
  };
  const getTotalPrice = () => {
    let total = 0;
    orderList.map(order => {
      total += order.price;
    });
    return total;
  };

  const totalCnt = useMemo(() => getTotalCnt());
  const totalPrice = useMemo(() => getTotalPrice());

  const cancelAll = () => {
    setOrderList([]);
  };

  return (
    <Grid item xs={3} style={{ height: 600, padding: 20 }}>
      <Typography>주문수량</Typography>
      <Paper className={classes.paper}>{totalCnt}</Paper>
      <Typography>주문금액</Typography>
      <Paper className={classes.paper}>{totalPrice}</Paper>
      <div>
        <Button className={classes.btnCancel} onClick={cancelAll}>
          전체취소
        </Button>
      </div>
      <div>
        <Button className={classes.btnPayment}>결제하기</Button>
      </div>
    </Grid>
  );
};

export default Payment;
