    beverages.map(beverage => {
      if (beverage.id === id) {
        temp.push({
          id: beverage.id,
          name: beverage.name,
          check: true,
          addPrice: beverage.addPrice
        });
        setBeveragePrice(beverage.addPrice);
      } else {
        temp.push({
          id: beverage.id,
          name: beverage.name,
          check: false,
          addPrice: beverage.addPrice
        });
      }
    });
    setBeverages(temp);
  };

  props.priceChanger(getTotalPrice());
  // console.log(sides[0].img);
