import React, { useState, useEffect, useMemo } from "react";
import {
  makeStyles,
  Grid,
  Paper,
  Typography,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from "@material-ui/core";
import { AddBox, IndeterminateCheckBox } from "@material-ui/icons";
import { grey } from "@material-ui/core/colors";

const useStyles = makeStyles(theme => ({
  text: {
    color: "white",
    padding: "0 auto",
    margin: "0"
  },
  paper: {
    padding: theme.spacing(1),
    textAlign: "center"
  },
  listPaper: {
    height: 600,
    paddingTop: 20,
    paddingLeft: 20
  }
}));

const OrderList = ({ order, orderList }) => {
  const classes = useStyles();

  // const [testList, setTestList] = useState([order]);

  // const orderRow = testList.map(order => (
  //   <TableRow>
  //     <TableCell>여기 제품명</TableCell>
  //     <TableCell style={{ textAlign: "center" }}>
  //       <IndeterminateCheckBox style={{ color: "red" }} />
  //       {order.cnt}
  //       <AddBox style={{ color: "red" }} />
  //     </TableCell>
  //     <TableCell style={{ textAlign: "center" }}>{order.price}</TableCell>
  //   </TableRow>
  // ));

  useEffect(
    () =>
      orderList.map(order => {
        console.log(order.contents);
      }),
    [orderList]
  );
  return (
    <Grid item xs={9} className={classes.listPaper}>
      <Paper style={{ height: 560 }}>
        <TableContainer>
          <TableHead>
            <TableRow style={{ background: grey[400] }}>
              <TableCell
                style={{ minWidth: 390, textAlign: "center", fontSize: 30 }}
              >
                제품명
              </TableCell>
              <TableCell
                style={{ minWidth: 200, textAlign: "center", fontSize: 30 }}
              >
                수량
              </TableCell>
              <TableCell
                style={{ minWidth: 200, textAlign: "center", fontSize: 30 }}
              >
                금액
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {/* <orderRow id="testOrder" /> */}
            {
              (orderList.map = order => (
                <TableRow key={order.cnt}>
                  <TableCell>여기 제품명</TableCell>
                  <TableCell style={{ textAlign: "center" }}>
                    <IndeterminateCheckBox style={{ color: "red" }} />
                    {order.cnt}
                    <AddBox style={{ color: "red" }} />
                  </TableCell>
                  <TableCell style={{ textAlign: "center" }}>
                    {order.price}
                  </TableCell>
                </TableRow>
              ))
            }
            <TableRow>
              <TableCell>여기 제품명</TableCell>
              <TableCell style={{ textAlign: "center" }}>
                <IndeterminateCheckBox style={{ color: "red" }} />
                2
                <AddBox style={{ color: "red" }} />
              </TableCell>
              <TableCell style={{ textAlign: "center" }}>200</TableCell>
            </TableRow>
          </TableBody>
        </TableContainer>
      </Paper>
    </Grid>
  );
};

export default OrderList;
