import React, { useState } from "react";
import { Redirect } from "react-router-dom";
import Layout from "../../layout/Layout";
import Webcam from "react-webcam";
var AWS = require("aws-sdk");
const videoConstraints = {
  width: 1280,
  height: 720,
  facingMode: "user"
};
const cfg = {
  region: "us-west-2",
  upload_bucket_name: "rekognition-stack-demo-s3upload-1gezpp4hs4cul",
  identity_pool_id: "us-west-2:811ae595-f007-467f-919f-0056e4a81b3b",
  face_collection: "rekognition-demo-go",
  ddb_table: "rekognition-demo-go"
};
var bucketName = cfg.upload_bucket_name;
var face_collection = cfg.face_collection;
var table = cfg.ddb_table;
// Define AWS Resources
var region = cfg.region;
var creds = new AWS.CognitoIdentityCredentials({
  IdentityPoolId: cfg.identity_pool_id
});

AWS.config.update({
  region: region,
  credentials: creds
});

var rekognition = new AWS.Rekognition({ apiVersion: "2016-06-27" });
var docClient = new AWS.DynamoDB.DocumentClient();

var identityId = AWS.config.credentials.identityId;

var s3 = new AWS.S3({
  apiVersion: "2006-03-01",
  params: { Bucket: bucketName }
});
const AuthPage = props => {
  const [issmile, setIssmile] = useState(false);
  const index = 0;
  var imageSrc;
  const webcamRef = React.useRef(null);
  const capture = React.useCallback(() => {
    imageSrc = getBinary(webcamRef.current.getScreenshot());
    console.log(imageSrc);
    trackEmotions();
  }, [webcamRef]);
  function getBinary(encodedFile) {
    var base64Image = encodedFile.split("data:image/jpeg;base64,")[1];
    var binaryImg = atob(base64Image);
    var length = binaryImg.length;
    var ab = new ArrayBuffer(length);
    var ua = new Uint8Array(ab);
    for (var i = 0; i < length; i++) {
      ua[i] = binaryImg.charCodeAt(i);
    }

    var blob = new Blob([ab], {
      type: "image/jpeg"
    });

    return ab;
  }
  function onTimeout() {
    var params = {
      CollectionId: face_collection,
      FaceMatchThreshold: 80,
      Image: {
        Bytes: blobData
      },
      MaxFaces: 10
    };
  }
  const trackEmotions = () => {
    var smileOrNot = false;

    console.log("감정 분석 들어갑니다.");
    var params = {
      Attributes: ["ALL"],
      Image: {
        Bytes: imageSrc
      }
    };
    rekognition.detectFaces(params, function(err, data) {
      if (err) {
        console.log("얼굴 인식 ERROR");
        console.log(err);
      } else {
        console.log(data);
        console.log(data.FaceDetails[0].Smile);
        var smile = data.FaceDetails[0].Smile;

        console.log(smile.Value);
        if (smile.Value === true) {
          smileOrNot = true;
          setIssmile(true);
          console.log("너는 웃고 있다..");
          //페이지 이동.
        }
      }
    });
  };
  const ChangeComp = () => {
    if (issmile) {
      return (
        <>
          <Redirect to="/Order" />
        </>
      );
    } else {
      return (
        <Layout>
          <Webcam
            audio={false}
            height={720}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            width={1280}
            videoConstraints={videoConstraints}
          />
          <button onClick={capture}>Capture photo</button>
        </Layout>
      );
    }
  };
  return <ChangeComp />;
  // <Layout>
  //   <Webcam
  //     audio={false}
  //     height={720}
  //     ref={webcamRef}
  //     screenshotFormat="image/jpeg"
  //     width={1280}
  //     videoConstraints={videoConstraints}
  //   />
  //   <button onClick={capture}>Capture photo</button>
  // </Layout>
};

export default AuthPage;
